mapper.select:
 select * from XYZYPZB Left JOIN BKTBCXCY 
ON XYZYPZB.XXDM=  BKTBCXCY.XXDM and XYZYPZB.XYDM=BKTBCXCY.XYDM and XYZYPZB.ZYIDXLSH=BKTBCXCY.ZYIDLSH  and BKTBCXCY.TBYEAR=#{arg1}
where BKTBCXCY.ZYIDLSH is null and XYZYPZB.ZYLB=1 and XYZYPZB.XXDM = #{arg0} ORDER BY XYZYPZB.XYDM

 select * from XYZYPZB Left JOIN ZKTBCXCY 
ON XYZYPZB.XXDM=  ZKTBCXCY.XXDM and XYZYPZB.XYDM=ZKTBCXCY.XYDM and XYZYPZB.ZYIDXLSH=ZKTBCXCY.ZYIDLSH and ZKTBCXCY.TBYEAR=#{arg1}
 where ZKTBCXCY.ZYIDLSH  is null and XYZYPZB.ZYLB=2 and XYZYPZB.XXDM = #{arg0}  ORDER BY XYZYPZB.XYDM
